import React, { useContext, useState } from 'react';
import { ShopContext } from '../../context/shop-context';

export const Product = (props) => {
    const { id,title, description, price, discountPercentage, rating, stock, brand, thumbnail, images } = props.data;
    const { addToCart, cartItems } = useContext(ShopContext);

    const cartItemAmount = cartItems[id]
    const discountedPrice = (price - (price * (discountPercentage / 100))).toFixed(2);

    const ImageSlider = ({ images }) => {
      const [currIndex, setCurrIndex] = useState(0);
    
      const goToPrevious = () => {
        const newIndex = currIndex === 0 ? images.length - 1 : currIndex - 1;
        setCurrIndex(newIndex);
      };
    
      const goToNext = () => {
        const newIndex = currIndex === images.length - 1 ? 0 : currIndex + 1;
        setCurrIndex(newIndex);
      };
    
      return (
        <div className="slider">
          <div className="leftArrow" onClick={goToPrevious}></div>
          <div className="rightArrow" onClick={goToNext}></div>
          <div className="slide">
            <img src={images[currIndex]} alt={`Slide ${currIndex}`} />
          </div>
        </div>
      );
    };
    

    const handleMouseEnter = () => {
      const slider = document.querySelector('.product-slider');
      slider.classList.add('show-slider');
    };
    
    const handleMouseLeave = () => {
      const slider = document.querySelector('.product-slider');
      slider.classList.remove('show-slider');
    };
    

  return (
    <div className="product">
      <div className="product-images">
        <img src={thumbnail} alt={`thumbnail for Product ${title}`} />
        {/* {images.map((image, index) => (
            <img key={index} src={image} alt={`Product ${id} Image ${index + 1}`} />
        ))} */}
      </div>
      

      <div className="description">
        <h2> {title} </h2>
        <p> {description} </p>
        <p>
          <span className="original-price">${price}</span>
          <span className="discounted-price">${discountedPrice}</span>
          <span className="discount">{discountPercentage}% off</span>
        </p>
        <p> Rating: {rating}  </p>
        <p> Stock: {stock} </p>
        <p> Brand: {brand} </p>
      </div>
      <button className="addToCartBttn" onClick={() => addToCart(id)}>
        Add to Cart {cartItemAmount > 0 && <> ({cartItemAmount})</>}
      </button>
    </div>
  )
}
