        placeholder="Type to search products..."
        onChange={e => setQuery(e.target.value)} />
      </div>

      <div className="products">
        {filteredProducts.map((product) => (
            <Product key={product.id} data={product} />
        ))}
      </div>
    </div>
   
  );
};
